mapper.select:
SELECT a.article_id AS id, a.title, a.content, IFNULL(f.access_url, '') AS coverUrl, CASE WHEN a.is_drafts = 1 THEN 0 ELSE 1 END AS status, IFNULL(( SELECT COUNT(*) FROM article_view av WHERE av.article_id = a.article_id ), 0) AS viewCount, IFNULL(( SELECT COUNT(*) FROM likes al WHERE al.target_id = a.article_id AND al.type = 'article' -- 添加类型条件 AND al.is_like = 1 ), 0) AS likeCount, IFNULL(( SELECT COUNT(*) FROM comments c WHERE c.article_id = a.article_id AND c.is_del = 0 ), 0) AS commentCount, DATE_FORMAT(a.created_at, '%Y-%m-%d %H:%i:%s') AS createTime, DATE_FORMAT(a.updated_at, '%Y-%m-%d %H:%i:%s') AS updateTime FROM article a LEFT JOIN file f ON a.file_id = f.file_id WHERE a.is_del = 1 AND a.user_id = #{loginUserId}
AND a.title LIKE CONCAT('%', #{dto.title}, '%')
AND a.is_drafts = #{dto.status}
ORDER BY
${dto.sortField} ${dto.isAsc ? 'ASC' : 'DESC'}
a.updated_at DESC
